import React from "react";

const ModalForm = ({
  isModalOpen,
  setIsModalOpen,
  newStory,
  handleInputChange,
  handleFormSubmit,
  recurrenceTypes,
  categories,
  generateUsername,
}) => {
  if (!isModalOpen) return null;

  return (
    <div className="modal">
      <div className="modal-content">
        <span
          className="modal-close"
          onClick={() => setIsModalOpen(false)}
          style={{
            position: "absolute",
            top: "10px",
            right: "15px",
            fontSize: "24px",
            color: "#fff",
            cursor: "pointer",
          }}
        >
          &times;
        </span>
        <h2>Add a New Story</h2>
        <form onSubmit={handleFormSubmit}>
          {/* Input for Name */}
          <div style={{ display: "flex", gap: "10px", alignItems: "center" }}>
            <input
              type="text"
              name="name"
              value={newStory.name}
              onChange={handleInputChange}
              placeholder="Use your name, nickname, or generate one"
              required
            />
            <button
              type="button"
              className="name-generator-button"
              onClick={generateUsername}
            >
              Generate Username
            </button>
          </div>

          {/* Input for Title */}
          <input
            type="text"
            name="title"
            value={newStory.title}
            onChange={handleInputChange}
            placeholder="Give your nightmare a title (max 30 characters)"
            maxLength={30}
            required
          />

          {/* Select for Recurrence */}
          <select
            name="recurrencia"
            value={newStory.recurrencia}
            onChange={handleInputChange}
            required
          >
            <option value="">How often do you dream about it?</option>
            {recurrenceTypes.map((type, index) => (
              <option key={index} value={type}>
                {type}
              </option>
            ))}
          </select>

          {/* Select for Category */}
          <select
            name="category"
            value={newStory.category}
            onChange={handleInputChange}
            required
          >
            <option value="">Which category fits best?</option>
            {categories.map((category) => (
              <option key={category.id} value={category.name}>
                {category.name}
              </option>
            ))}
          </select>

          {/* Input for Email */}
          <input
            type="email"
            name="email"
            value={newStory.email}
            onChange={handleInputChange}
            placeholder="Email: We will not publish your email, only in case of contact"
            required
          />

          {/* Textarea for Story Value */}
          <textarea
            name="value"
            value={newStory.value}
            onChange={handleInputChange}
            placeholder="Describe your nightmare"
            required
          />

          {/* Accept Terms Checkbox */}
          <div
            style={{
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              gap: "10px",
            }}
          >
            <input
              type="checkbox"
              name="acceptTerms"
              required
              style={{
                width: "16px",
                height: "16px",
                cursor: "pointer",
              }}
            />
            <label
              style={{
                fontSize: "14px",
                lineHeight: "1.5",
                margin: 0,
              }}
            >
              I have read and accept the{" "}
              <a
                href="/terms"
                target="_blank"
                rel="noopener noreferrer"
                style={{
                  color: "#fff",
                  textDecoration: "underline",
                  fontWeight: "bold",
                }}
              >
                terms and conditions
              </a>
              .
            </label>
          </div>

          {/* Submit and Cancel Buttons */}
          <div style={{ display: "flex", gap: "10px", justifyContent: "center" }}>
            <button type="submit">Add Story</button>
            <button type="button" onClick={() => setIsModalOpen(false)}>
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ModalForm;
