import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from './Components/Header';
import Hero from './Components/Hero';
import CategoriesCarousel from './Components/CategoriesCarousel';
import StoriesHome from './Components/StoriesHome';
import ModalForm from './Components/ModalForm';

function App() {
  const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3000/api/v1/';
  const [isModalOpen, setIsModalOpen] = useState(false);

  return (
    <Router>
      <div>
        <Header />
        <Routes>
          <Route
            path="/"
            element={
              <>
                <Hero onOpenModal={() => setIsModalOpen(true)} />
                <CategoriesCarousel API_BASE_URL={API_BASE_URL} />
                <StoriesHome API_BASE_URL={API_BASE_URL} />
              </>
            }
          />
          <Route
            path="/stories"
            element={<StoriesHome API_BASE_URL={API_BASE_URL} />}
          />
          <Route
            path="/categories"
            element={<CategoriesCarousel API_BASE_URL={API_BASE_URL} />}
          />
        </Routes>
        <ModalForm isModalOpen={isModalOpen} setIsModalOpen={setIsModalOpen} />
      </div>
    </Router>
  );
}

export default App;
