import React, { useState, useEffect } from 'react';
import './Categories.css'; // Archivo CSS para estilos personalizados

const Categories = () => {
  const [categories, setCategories] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  // Cambia la URL según la API que estés usando
  const API_URL = 'http://localhost:4000/api/categories';

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch(API_URL);
        if (!response.ok) {
          throw new Error('Error al obtener las categorías');
        }
        const data = await response.json();
        setCategories(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setIsLoading(false);
      }
    };

    fetchCategories();
  }, []);

  if (isLoading) {
    return <p className="text-center text-white">Cargando categorías...</p>;
  }

  if (error) {
    return <p className="text-center text-danger">{error}</p>;
  }

  return (
    <section className="categories py-5">
      <div className="container">
        <h2 className="text-center text-white mb-4">Categorías</h2>
        <div className="categories-grid">
          {categories.map((category) => (
            <div key={category.id} className="category-card">
              <img
                src={category.imageUrl}
                alt={category.name}
                className="img-fluid rounded mb-3"
              />
              <h3 className="text-white">{category.name}</h3>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Categories;
