import React, { useState, useEffect } from "react";
import "./StoriesPage.css";
import StoryCard from "../Components/StoryCard";
import Background from "../Components/Background";
import Filter from "../Components/Filter"; // Importar el filtro

const StoriesPage = ({ API_BASE_URL }) => {
  const [stories, setStories] = useState([]);
  const [filteredStories, setFilteredStories] = useState([]); // Para las historias filtradas
  const [categories, setCategories] = useState([]); // Categorías disponibles
  const [selectedCategory, setSelectedCategory] = useState(""); // Categoría seleccionada
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStories = async () => {
      try {
        const response = await fetch(`${API_BASE_URL}notes`);
        if (!response.ok) {
          throw new Error("Failed to fetch stories");
        }
        const data = await response.json();

        // Ordenar por ID descendente
        const sortedStories = data.sort((a, b) => b.id - a.id);
        setStories(sortedStories);

        // Extraer categorías únicas
        const uniqueCategories = [
          ...new Set(sortedStories.map((story) => story.category)),
        ];
        setCategories(uniqueCategories);

        setFilteredStories(sortedStories); // Inicialmente, todas las historias
        setLoading(false);
      } catch (err) {
        console.error("Error fetching stories:", err);
        setError("Unable to load stories. Please try again later.");
        setLoading(false);
      }
    };

    fetchStories();
  }, [API_BASE_URL]);

  // Filtrar historias cuando cambie la categoría seleccionada
  useEffect(() => {
    if (selectedCategory) {
      const filtered = stories.filter(
        (story) => story.category === selectedCategory
      );
      setFilteredStories(filtered);
    } else {
      setFilteredStories(stories);
    }
  }, [selectedCategory, stories]);

  if (loading) {
    return <p className="loading-message">Loading stories...</p>;
  }

  if (error) {
    return <p className="error-message">{error}</p>;
  }

  return (
    <section className="stories-page">
      <div className="container py-4">
        <h1 className="text-center text-light page-title">All Stories</h1>
        {/* Filtro */}
        <Filter
          categories={categories}
          selectedCategory={selectedCategory}
          setSelectedCategory={setSelectedCategory}
        />
        {/* Historias */}
        <div className="stories-grid">
          <Background />
          {filteredStories.map((story) => (
            <StoryCard key={story.id} story={story} />
          ))}
        </div>
      </div>
    </section>
  );
};

export default StoriesPage;
