import React, { useState, useEffect } from "react";
import "./StoriesPage.css";
import StoryCard from "../Components/StoryCard";
import Background from "../Components/Background";
import Filter from "../Components/Filter";

const StoriesPage = ({ API_BASE_URL }) => {
  const [stories, setStories] = useState([]);
  const [filteredStories, setFilteredStories] = useState([]);
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStories = async () => {
      try {
        const response = await fetch(`${API_BASE_URL}notes`);
        if (!response.ok) {
          throw new Error("Failed to fetch stories");
        }
        const data = await response.json();
        const sortedStories = data.sort((a, b) => b.id - a.id);
        setStories(sortedStories);
        setFilteredStories(sortedStories); // Por defecto, muestra todas las historias
        setLoading(false);
      } catch (err) {
        console.error("Error fetching stories:", err);
        setError("Unable to load stories. Please try again later.");
        setLoading(false);
      }
    };

    fetchStories();
  }, [API_BASE_URL]);

  const handleCategoryChange = (category) => {
    // Actualizar las categorías seleccionadas
    const updatedSelectedCategories = selectedCategories.includes(category)
      ? selectedCategories.filter((cat) => cat !== category)
      : [...selectedCategories, category];

    setSelectedCategories(updatedSelectedCategories);

    // Filtrar historias según las categorías seleccionadas
    if (updatedSelectedCategories.length > 0) {
      const filtered = stories.filter((story) =>
        updatedSelectedCategories.includes(story.category)
      );
      setFilteredStories(filtered);
    } else {
      setFilteredStories(stories); // Si no hay categorías seleccionadas, muestra todas
    }
  };

  if (loading) {
    return <p className="loading-message">Loading stories...</p>;
  }

  if (error) {
    return <p className="error-message">{error}</p>;
  }

  const categories = [
    "Apocalyptic",
    "Slasher",
    "Psychological",
    "Cosmic Horror",
    "Haunted House",
    "Technological Horror",
    "Supernatural",
    "Gore",
    "Dark Tales",
  ];

  return (
    <section className="stories-page">
      <div className="container py-4">
        <h1 className="text-center text-light page-title">All Stories</h1>
        <h5 className="text-center text-light">Filter by Categories:</h5>
        <Filter
          categories={categories}
          selectedCategories={selectedCategories}
          onCategoryChange={handleCategoryChange} // Pasar correctamente la función
        />
        <div className="stories-grid">
          <Background />
          {filteredStories.length > 0 ? (
            filteredStories.map((story) => (
              <StoryCard key={story.id} story={story} />
            ))
          ) : (
            <p className="text-light text-center">No stories match the selected filters.</p>
          )}
        </div>
      </div>
    </section>
  );
};

export default StoriesPage;
