import React from 'react';
import { Routes, Route } from 'react-router-dom';
import Hero from './Hero';
import CategoriesCarousel from './CategoriesCarousel';
import StoriesHome from './StoriesHome';
import ModalForm from './ModalForm';

const AppRoutes = ({ API_BASE_URL, isModalOpen, setIsModalOpen }) => {
  return (
    <Routes>
      {/* Ruta principal */}
      <Route
        path="/"
        element={
          <>
            <Hero onOpenModal={() => setIsModalOpen(true)} />
            <CategoriesCarousel API_BASE_URL={API_BASE_URL} />
            <StoriesHome API_BASE_URL={API_BASE_URL} />
          </>
        }
      />

      {/* Ruta para historias */}
      <Route
        path="/stories"
        element={<StoriesHome API_BASE_URL={API_BASE_URL} />}
      />

      {/* Ruta para categor√≠as */}
      <Route
        path="/categories"
        element={<CategoriesCarousel API_BASE_URL={API_BASE_URL} />}
      />
    </Routes>
  );
};

export default AppRoutes;
