import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import StoriesHome from "./StoriesHome";
import ModalForm from "./ModalForm";
import CategoriesCarousel from "./CategoriesCarousel";
import Header from "./Header";

const AppRoutes = ({ API_BASE_URL }) => {
  return (
    <Router>
      <Header />
      <Routes>
        {/* Rutas principales */}
        <Route
          path="/"
          element={<StoriesHome API_BASE_URL={API_BASE_URL} />}
        />
        <Route
          path="/stories"
          element={<CategoriesCarousel API_BASE_URL={API_BASE_URL} />}
        />
        <Route path="/submit" element={<ModalForm />} />
        {/* Agrega más rutas aquí según necesites */}
      </Routes>
    </Router>
  );
};

export default AppRoutes;
