import React from "react";
import categoriesImages from "../img/categories/categories";
import "./Filter.css";

const Filter = ({ categories, selectedCategories = [], onCategoryChange }) => {
  return (
    <div className="filter-container">
      {categories.map((category) => {
        const categoryKey = category.toLowerCase().replace(/\s+/g, "_");
        const image = categoriesImages[categoryKey];

        return (
          <div
            key={category}
            className={`filter-option ${
              selectedCategories.includes(category) ? "selected" : ""
            }`}
            onClick={() => onCategoryChange(category)} // Asegúrate de que se pase correctamente la función
          >
            {image && (
              <img src={image} alt={category} className="filter-image" />
            )}
            <span className="filter-label">{category}</span>
          </div>
        );
      })}
    </div>
  );
};

export default Filter;
