import React from 'react';
import Header from '../Components/Header'; // Ruta correcta del Header
import Hero from '../Components/Hero'; // Reutilizamos el Hero
import './Terms.css'; // Archivo CSS para los estilos específicos

const TermsPage = () => {
  return (
    <div className="terms-page">
      <div style={styles.heroContainer}>
        <Hero />
        <div className="pdf-container" style={styles.pdfContainer}>
          <iframe
            src="/Terms_and_Conditions_of_Use.pdf" // Ruta correcta del PDF
            title="Terms and Conditions"
            style={styles.iframe}
          />
        </div>
      </div>
    </div>
  );
};

const styles = {
  body: {
    overflowY: 'hidden'
  },
  heroContainer: {
    position: 'relative', // Posicionamiento relativo al contenedor Hero
    height: 'auto', // Ajusta automáticamente la altura según el contenido
    overflow: 'hidden', // Evita desbordamiento
    },
  pdfContainer: {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    width: '50%',
    height: '80%',
    zIndex: 10,
    backgroundColor: 'rgba(0, 0, 0, 0.8)',
    boxShadow: '0 4px 10px rgba(0, 0, 0, 0.5)',
    borderRadius: '8px',
    margin: 0, // Elimina márgenes
    padding: 0, // Elimina padding
  },
  iframe: {
    width: '100%',
    height: '100%',
    border: 'none',
    display: 'block', // Elimina espacio blanco extra
    maxWidth: '100%', // Evita que se salga del contenedor
    boxSizing: 'border-box', // Asegura que padding y border se consideren en el tamaño total
  },
};

export default TermsPage;
