import React from "react";
import categoriesImages from "../img/categories/categories"; // Usa el archivo existente
import "./Filter.css";

const Filter = ({ categories, selectedCategories, handleCategoryChange }) => {
  return (
    <div className="filter-container">
      {categories.map((category) => {
        const categoryKey = category.toLowerCase().replace(/\s+/g, "_"); // Convierte a la clave del archivo
        const image = categoriesImages[categoryKey];

        return (
          <div
            key={category}
            className={`filter-option ${
              selectedCategories.includes(category) ? "selected" : ""
            }`}
            onClick={() => handleCategoryChange(category)}
          >
            {image && (
              <img
                src={image}
                alt={category}
                className="filter-image"
              />
            )}
            <span className="filter-label">{category}</span>
          </div>
        );
      })}
    </div>
  );
};

export default Filter;
