import React from "react";
import "./StoriesExpanded.css";

const StoriesExpanded = ({ story, onClose }) => {
  if (!story) return null;

  return (
    <div className="stories-expanded-overlay">
      <div className="stories-expanded-container">
        <button className="stories-expanded-close" onClick={onClose}>
          &times;
        </button>
        <h1 className="story-title">{story.title}</h1>
        <p className="story-category">
          <strong>Category:</strong> {story.category}
        </p>
        <p className="story-recurrence">
          <strong>Recurrence:</strong> {story.recurrence}
        </p>
        <p className="story-recurrence">
          <strong>Details:</strong> {story.value}
        </p>
        <div className="story-description">{story.description}</div>
      </div>
    </div>
  );
};

export default StoriesExpanded;
