import React, { useState } from "react";
import ModalForm from "./ModalForm";

const ModalButton = ({ recurrenceTypes, categories }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [newStory, setNewStory] = useState({
    name: "",
    title: "",
    recurrencia: "",
    category: "",
    email: "",
    value: "",
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewStory((prevStory) => ({ ...prevStory, [name]: value }));
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    console.log("Story submitted:", newStory);
    setIsModalOpen(false);
    setNewStory({
      name: "",
      title: "",
      recurrencia: "",
      category: "",
      email: "",
      value: "",
    });
  };

  const generateUsername = () => {
    const randomUsername = `User${Math.floor(Math.random() * 1000)}`;
    setNewStory((prevStory) => ({ ...prevStory, name: randomUsername }));
  };

  return (
    <>
      {/* Bot√≥n para abrir el modal */}
      <button
        className="btn btn-primary"
        onClick={() => setIsModalOpen(true)}
      >
        Submit Your Story
      </button>

      {/* Modal */}
      <ModalForm
        isModalOpen={isModalOpen}
        setIsModalOpen={setIsModalOpen}
        newStory={newStory}
        handleInputChange={handleInputChange}
        handleFormSubmit={handleFormSubmit}
        recurrenceTypes={recurrenceTypes}
        categories={categories}
        generateUsername={generateUsername}
      />
    </>
  );
};

export default ModalButton;
