import React, { useState, useEffect } from "react";
import "./StoriesPage.css"; // Archivo CSS para estilos
import StoryCard from "../Components/StoryCard"; // Suponiendo que usas un componente para mostrar historias
import Background from "../Components/Background";

const StoriesPage = ({ API_BASE_URL }) => {
  const [stories, setStories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStories = async () => {
      try {
        const response = await fetch(`${API_BASE_URL}notes`);
        if (!response.ok) {
          throw new Error("Failed to fetch stories");
        }
        const data = await response.json();
        setStories(data);
        setLoading(false);
      } catch (err) {
        console.error("Error fetching stories:", err);
        setError("Unable to load stories. Please try again later.");
        setLoading(false);
      }
    };

    fetchStories();
  }, [API_BASE_URL]);

  if (loading) {
    return (
      <div className="loading-container">
        <p className="loading-message">Loading stories...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <p className="error-message">{error}</p>
      </div>
    );
  }

  return (
    <div className="stories-page">
      {/* Fondo din√°mico */}
      <Background />

      {/* Contenido principal */}
      <div className="container py-4">
        <h1 className="text-center text-light page-title">All Stories</h1>
        <div className="stories-grid">
          {stories.map((story) => (
            <StoryCard key={story.id} story={story} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default StoriesPage;
