import React, { useState, useEffect } from "react";
import "./StoriesPage.css";
import StoryCard from "../Components/StoryCard";
import Background from "../Components/Background";
import Filter from "../Components/Filter";
import StoriesExpanded from "../Components/StoriesExpanded";
import categoriesImages from "../img/categories/categories"; // Importa las imágenes de categorías

const StoriesPage = ({ API_BASE_URL }) => {
  const [stories, setStories] = useState([]);
  const [filteredStories, setFilteredStories] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [selectedStory, setSelectedStory] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch de historias
  useEffect(() => {
    const fetchStories = async () => {
      try {
        const response = await fetch(`${API_BASE_URL}notes`);
        if (!response.ok) {
          throw new Error("Failed to fetch stories");
        }
        const data = await response.json();

        // Ordena historias por ID y extrae categorías únicas
        const sortedStories = data.sort((a, b) => b.id - a.id);
        const uniqueCategories = [
          ...new Set(sortedStories.map((story) => story.category)),
        ];

        setStories(sortedStories);
        setFilteredStories(sortedStories);
        setCategories(uniqueCategories);

        setLoading(false);
      } catch (err) {
        console.error("Error fetching stories:", err);
        setError("Unable to load stories. Please try again later.");
        setLoading(false);
      }
    };

    fetchStories();
  }, [API_BASE_URL]);

  // Filtra historias según categorías seleccionadas
  useEffect(() => {
    if (selectedCategories.length === 0) {
      setFilteredStories(stories);
    } else {
      const filtered = stories.filter((story) =>
        selectedCategories.includes(story.category)
      );
      setFilteredStories(filtered);
    }
  }, [selectedCategories, stories]);

  // Manejo de clics en historias
  const handleStoryClick = (story) => {
    setSelectedStory(story);
  };

  // Cerrar ventana flotante
  const handleCloseExpanded = () => {
    setSelectedStory(null);
  };

  // Obtiene el fondo dinámico según las categorías seleccionadas
  const getBackgroundImage = () => {
    if (selectedCategories.length === 1) {
      const categoryKey = selectedCategories[0].toLowerCase().replace(/\s+/g, "_");
      return categoriesImages[categoryKey];
    }
    return categoriesImages["default"]; // Imagen predeterminada
  };

  if (loading) {
    return <p className="loading-message">Loading stories...</p>;
  }

  if (error) {
    return <p className="error-message">{error}</p>;
  }

  return (
    <section
      className="stories-page"
      style={{
        backgroundImage: `url(${getBackgroundImage()})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        filter: "blur(8px)", // Agrega un desenfoque al fondo
      }}
    >
      <div className="stories-content">
        <Background />
        <div className="container py-4">
          <h1 className="text-center text-light page-title">All Stories</h1>
          <Filter
            categories={categories}
            selectedCategories={selectedCategories}
            setSelectedCategories={setSelectedCategories}
          />
          <div className="stories-grid">
            {filteredStories.map((story) => (
              <div key={story.id} onClick={() => handleStoryClick(story)}>
                <StoryCard story={story} />
              </div>
            ))}
          </div>
        </div>

        {/* Ventana flotante */}
        {selectedStory && (
          <StoriesExpanded
            storyId={selectedStory.id}
            onClose={handleCloseExpanded}
            API_BASE_URL={API_BASE_URL}
          />
        )}
      </div>
    </section>
  );
};

export default StoriesPage;
