import React, { useState } from "react";
import AppRoutes from "./Components/Routes";
import FloatingButton from "./Components/FloatingButton";
import ModalForm from "./Components/ModalForm"; // Asumiendo que este es el modal para el formulario

function App() {
  const API_BASE_URL = process.env.REACT_APP_API_URL || "http://localhost:3000/api/v1/";
  const [isModalOpen, setIsModalOpen] = useState(false);

  return (
    <div>
      {/* Rutas de la aplicación */}
      <AppRoutes API_BASE_URL={API_BASE_URL} isModalOpen={isModalOpen} setIsModalOpen={setIsModalOpen} />

      {/* Botón flotante global */}
      <FloatingButton onClick={() => setIsModalOpen(true)} />

      {/* Modal del formulario */}
      {isModalOpen && <ModalForm isModalOpen={isModalOpen} setIsModalOpen={setIsModalOpen} />}
    </div>
  );
}

export default App;
