import React from "react";
import categoriesImages from "../img/categories/categories";
import "./Filter.css";

const Filter = ({ categories, selectedCategories, setSelectedCategories }) => {
  const handleCategoryChange = (category) => {
    const updatedCategories = selectedCategories.includes(category)
      ? selectedCategories.filter((cat) => cat !== category)
      : [...selectedCategories, category];
    setSelectedCategories(updatedCategories);
  };

  return (
    <div className="dropdown">
      <button
        className="btn btn-dark dropdown-toggle"
        type="button"
        id="dropdownMenuButton"
        data-bs-toggle="dropdown"
        aria-expanded="false"
      >
        Filter by Categories
      </button>
      <ul className="dropdown-menu dropdown-menu-dark" aria-labelledby="dropdownMenuButton">
        {categories.map((category) => {
          const categoryKey = category.toLowerCase().replace(/\s+/g, "_");
          const image = categoriesImages[categoryKey];

          return (
            <li key={category} className="dropdown-item">
              <div
                className={`filter-option ${
                  selectedCategories.includes(category) ? "selected" : ""
                }`}
                onClick={() => handleCategoryChange(category)}
              >
                {image && (
                  <img src={image} alt={category} className="filter-image me-2" />
                )}
                <span className="filter-label">{category}</span>
              </div>
            </li>
          );
        })}
      </ul>
    </div>
  );
};

export default Filter;
