import React from "react";
import categoriesImages from "../img/categories/categories";
import "./Filter.css";

const Filter = ({ categories, selectedCategories, setSelectedCategories }) => {
  const handleCategoryChange = (category) => {
    const updatedCategories = selectedCategories.includes(category)
      ? selectedCategories.filter((cat) => cat !== category) // Eliminar si ya está seleccionado
      : [...selectedCategories, category]; // Agregar si no está seleccionado
    setSelectedCategories(updatedCategories); // Actualizar las categorías seleccionadas
  };

  return (
    <div className="filter-container">
      {categories.map((category) => {
        const categoryKey = category.toLowerCase().replace(/\s+/g, "_");
        const image = categoriesImages[categoryKey];

        return (
          <div
            key={category}
            className={`filter-option ${
              selectedCategories.includes(category) ? "selected" : ""
            }`}
            onClick={() => handleCategoryChange(category)}
          >
            {image && <img src={image} alt={category} className="filter-image" />}
            <span className="filter-label">{category}</span>
          </div>
        );
      })}
    </div>
  );
};

export default Filter;
