import React from 'react';
import '../App.css';
import './StoriesGrid.css'; // Archivo CSS específico para las historias

const StoriesGrid = ({ stories, apiError }) => {
  return (
    <section className="stories-section py-5">
      <div className="container">
        <h2 className="text-center text-white mb-4">Stories</h2>
        {apiError ? (
          <p className="error-message text-danger text-center">{apiError}</p>
        ) : (
          <div className="stories-grid">
            {stories.map((story) => (
              <div key={story.id} className="story-card">
                <img
                  src={story.image || '/path/to/default_image.png'}
                  alt={story.title}
                  className="img-fluid story-image"
                />
                <h3 className="story-title">{story.title}</h3>
                <p className="story-category">
                  <strong>Category:</strong> {story.category}
                </p>
                <p className="story-recurrence">
                  <strong>Recurrence:</strong> {story.recurrencia || 'N/A'}
                </p>
                <p className="story-description">
                  <strong>Description:</strong> {story.value}
                </p>
                <button className="btn btn-primary btn-sm">Read More</button>
              </div>
            ))}
          </div>
        )}
      </div>
    </section>
  );
};

export default StoriesGrid;
