import React from "react";
import "./Filter.css";
import categoriesImages from './img/categories/categories'; // Asegúrate de tener un mapeo de imágenes

const Filter = ({ categories, selectedCategories, setSelectedCategories }) => {
  const handleCategoryChange = (category) => {
    if (selectedCategories.includes(category)) {
      // Elimina la categoría si ya está seleccionada
      setSelectedCategories(
        selectedCategories.filter((item) => item !== category)
      );
    } else {
      // Agrega la categoría si no está seleccionada
      setSelectedCategories([...selectedCategories, category]);
    }
  };

  return (
    <div className="filter-container">
      <h3 className="filter-title text-light">Filter by Categories:</h3>
      <div className="filter-options">
        {categories.map((category) => (
          <div
            key={category}
            className={`filter-option ${
              selectedCategories.includes(category) ? "selected" : ""
            }`}
            onClick={() => handleCategoryChange(category)}
          >
            <img
              src={categoriesImages[category]}
              alt={category}
              className="filter-image"
            />
            <span className="filter-label">{category}</span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Filter;
