import React from "react";
import categoriesImages from "../img/categories/categories";
import "./StoryCard.css";

const StoryCard = ({ story, inStoriesPage = false }) => {
  const categoryKey = story.category.toLowerCase().replace(/\s+/g, "_");
  const categoryImage = categoriesImages[categoryKey];

  return (
    <div
      className={`story-card ${inStoriesPage ? "blur-background" : ""}`}
      style={{
        backgroundImage: inStoriesPage ? `url(${categoryImage})` : "none",
      }}
    >
      <div className="story-content">
        <h3 className="story-title">{story.title}</h3>
        <p className="story-category">
          <strong>Category:</strong> {story.category}
        </p>
        <p className="story-description">
          <strong>Description:</strong> {story.value.slice(0, 100)}...
        </p>
      </div>
    </div>
  );
};

export default StoryCard;
